import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';

export default function Home() {
  const navigate = useNavigate();
  const isRegistered = localStorage.getItem('isRegistered');

  // Check if user is registered
  useEffect(() => {
    if (!isRegistered) {
      navigate('/register');
    }
  }, [isRegistered, navigate]);

  // State for storing data
  const [data, setData] = useState([]);

  // Function to load data
  const loadData = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/get");
      setData(response.data);
    } catch (error) {
      console.error("Error loading data:", error);
    }
  }

  // Load data on component mount
  useEffect(() => {
    loadData();
  }, []);

  // Function to delete user
  const deleteUser = (id) => {
    if (window.confirm("Are you sure?")) {
      axios.delete(`http://localhost:5000/delete/${id}`)
        .then(() => setTimeout(loadData, 500))
        .catch(error => console.error("Error deleting user:", error));
    }
  };

  // JSX for rendering the component
  return (
    <div style={{ backgroundColor: 'black', color: 'white', minHeight: 800 }}>
      <h1>Task Management</h1>
      <h2>Add Your Task</h2>
      <Link to="/addUser">
        <button style={{ height: 40, width: 250 }}>Add New User</button>
      </Link>
      <br /><br />
      <table border="1" style={{ margin: "0 auto", width: "80%" }}>
        <thead>
          <tr>
            <th style={{ textAlign: 'center' }}>Id</th>
            <th style={{ textAlign: 'center' }}>Name</th>
            <th style={{ textAlign: 'center' }}>Address</th>
            <th style={{ textAlign: 'center' }}>Contact No</th>
            <th style={{ textAlign: 'center' }}>Email Address</th>
            <th style={{ textAlign: 'center' }}>Update</th>
            <th style={{ textAlign: 'center' }}>Delete</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item, index) => (
            <tr key={item.id}>
              <th scope='row'>{index + 1}</th>
              <td>{item.name}</td>
              <td>{item.address}</td>
              <td>{item.contactno}</td>
              <td>{item.emailaddress}</td>
              <td>
                <Link to={`/update/${item.id}`}>
                  <button>Update</button>
                </Link>
              </td>
              <td>
                <button onClick={() => deleteUser(item.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
