import React, {useEffect, useState} from 'react'
import {useHistory,useParams, Link} from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';

const initialState ={
    name:"",
    gender:"",
    address:"",
    contactno:"",
    emailaddress:"",
}

export default function AddEdit() {
    const [state,setState]=useState(initialState);

    const{name,gender,address,contactno,emailaddress}=state;
    const history=userHistory();
    const handleSubmit=(e)=>{
        e.preventDefault();
        axios.post("http://localhost:5000/api/post",
        {name,gender,address,contactno,emailaddress}).then(()=>{
            setState({name:"",gender:"",address:"",contactno:"",emailaddress:""});}).catch((err)=>
            toast.error(err.response.data));
            setTimeout(()=>history.push("/"),500);
        };
    
    const handleInputChange=(e)=>{
        const {name,value}=e.target;
        setState({...state, [name]:value});
    };
  return (
    <div>
      <h2>Add Edit</h2>
      <form style={{
        margin:"auto",
        padding:"15px",
        maxWidth:"400px",
        alignContent:"center"
      }} onSubmit={handleSubmit}>
        <label htmlFor='name'>Name</label>
        <input type="text" name="name" value={name} onChange={handleInputChange}/><br/><br/>
        <label htmlFor='gender'>Gender</label>
        <input type="radio" name="gender" value="Male" onChange={handleInputChange}/> Male
        <input type="radio" name="gender" value="Female" onChange={handleInputChange}/> Female
        <br/><br/>
        <label htmlFor='address'>Address</label>
        <textarea name="address" onChange={handleInputChange} value={address}></textarea><br/><br/>
        <label htmlFor='contactno'>Contact No</label>
        <input type="number" name="contactno" onChange={handleInputChange} value={contactno}/><br/><br/>
        <label htmlFor='emailaddress'>Email Address</label>
        <input type="email" name="emailaddress" value={emailaddress} onChange={handleInputChange}/><br/><br/>
        <input type="submit"/><br/><br/>
        <Link to="/">
            <input type="button" value="Back"/>
        </Link>
      </form>
    </div>
  )
}
