import React, { useEffect, useState } from 'react'
import { useNavigate, useParams, Link } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';

const initialState = {
  name: "",
  address: "",
  contactno: "",
  emailaddress: "",
}

export default function AddEdit() {
  const [state, setState] = useState(initialState);

  const { name, address, contactno, emailaddress } = state;
  const navigate = useNavigate();
  const handleSubmit = (e) => {
    e.preventDefault();
    if (!name || !address || !contactno || !emailaddress) {
      toast.error("Please fill all fields");
    }
    else {
      axios
        .post("http://localhost:5000/api/post", {
          name,
          address,
          contactno,
          emailaddress,
        })
        .then(() => {
          setState({ name: "", address: "", contactno: "", emailaddress: "" });
        })
        .catch((err) =>
          toast.error(err.response.data));
      setTimeout(() => navigate("/"), 500);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setState({ ...state, [name]: value });
  };
  return (
    <div style={{ background: "blue", height: 1000, color: 'white', display: 'flex', alignItems: 'center', flexDirection: 'column' }}>
      <h1>Update</h1>
      <form style={{
        padding: "15px",
        
      }} onSubmit={handleSubmit}>
        <table>
          <tr style={{height:100}}>
            <td>
              <label htmlFor='name'>Name</label>
            </td>
            <td>

              <input style={{height:30,width:300}} type="text" name="name" value={name} onChange={handleInputChange} /><br /><br />

            </td>
          </tr>
          <tr>
            <td>
              <label htmlFor='address'>Address</label>
            </td>
            <td>
              <textarea style={{height:30,width:300}} name="address" onChange={handleInputChange} value={address}></textarea><br /><br />

            </td>
          </tr>
          <tr>
            <td>
              <label htmlFor='contactno'>Contact No</label>

            </td>
            <td>
              <input style={{height:30,width:300}} type="number" name="contactno" onChange={handleInputChange} value={contactno} /><br /><br />

            </td>
          </tr>

          <tr>
            <td>
              <label htmlFor='emailaddress'>Email Address</label>

            </td>
            <td>

              <input style={{height:30,width:300}} type="email" name="emailaddress" value={emailaddress} onChange={handleInputChange} /><br /><br />

            </td>
          </tr>
          <tr style={{border}}>
            <td>
              <input style={{height:30,width:300,marginTop:6}}  type="submit" /><br /><br />
            </td>

            <td>
              <Link to="/">
                <input style={{height:30,width:300}} type="button" value="Back" />
              </Link>
            </td>
          </tr>
        </table>
      </form>
    </div>
  )
}
